import { useState } from "react";
import "./App.css";
import { sampleText } from "./sampleText";

function App() {
  const [text, setText] = useState("test");

  function test() {
    setText(sampleText);
  }

  return (
    <div className="container">
      <div className="row">
        <div className="col-sm-6">
          <textarea rows="35" className="form-control" value={text} />
        </div>
        <div className="col-sm-6">
          <div>{text}</div>
        </div>
      </div>
    </div>
  );
}

export default App;
